INSERT INTO `genre` (`idGenre`, `nameGenre`, `descGenre`) VALUES
(1, 'Екшн', 'жанр відеоігор, який вимагає від гравця певних фізичних зусиль, таких як швидкості реакції та здібно'),
(2, 'Стратегія', 'Сенс стратегічних ігор полягає в плануванні дій та виробленні певної стратегії для досягнення якоїсь'),
(3, 'Рольова гра', 'Гравець асоціюється з конкретним персонажем або лідером команди, які діють відповідно до правил свої'),
(4, 'Симулятор', 'В широкому розумінні всі ігри є симуляторами. У вужчому значенні це відеоігри, призначені для склада'),
(5, 'Open world', 'В пригодницьких відеоіграх гравець керує ігровим персонажем, який рухається по сюжету та виконує зум'),
(6, 'Перегони', 'Перего́ни — це жанр відеоігор, в іграх якого гравець бере участь у перегонах на будь-якому виді тран');
INSERT INTO `hero` (`idHero`, `nameHero`, `genderHero`, `roleHero`, `descHero`) VALUES
(1, 'Карл Джонсон «CJ»', 'Ч', 'Член банди', 'Кендл Джонсон є сестрою лідерів банди \"Гроув стріт фемеліс\" Карла і Шона Джонсонів, а також померлого Браяна Джонсона. Вона має хлопця-латиноамериканця з банди \"Варріо Лос Ацтекас\" на ім\'я Цезар Віальпандо. Кендл має великий підприємницький талант та лідерські якості. На відміну від братів, для яких кримінальна діяльність є необхідною для виживання, вона могла би себе гарно реалізувати у законній діяльності, особливо у бізнесі.'),
(2, 'Джейсон Броді', 'Ч', 'Виживший турист', 'протагонист Far Cry 3. Джейсон был схвачен пиратами в плен вместе со своими друзьями после прыжка с парашютом над островами Рук, однако ему удалось сбежать из плена, после чего он начинает своё путешествие по острову в поисках друзей.'),
(3, 'Ваас Монтенеґро', 'Ч', 'Лідер піратів, психопат і наркоман', 'второстепенный антагонист Far Cry 3, психопат и наркоман, живущий на Северном острове Рук и работающий на Хойта Волкера. Является лидером пиратов.'),
(4, 'Вито Скалетта', 'Ч', 'Мафіозі, солдат', 'основной протагонист игры «Mafia Ⅱ» и один из ключевых персонажей «Mafia Ⅲ». Как и его друг Джо Барбаро, тоже является мафиози. В третьей части игры становится одним из «лейтенантов» Линкольна и возглавляет фракцию итальянцев.'),
(5, 'Джозеф «Джо» Барбаро', 'Ч', 'Злочинець', 'один из главных героев игры «Mafia Ⅱ» и главный герой «Joe’s Adventures», лучший друг Вито Скалетты. профессиональный преступник и закадычный друг Вито Скалетты. Наглый и непредсказуемый, Джо, кажется, способен устроить проблемы из ничего.'),
(6, 'Леонардо «Лео» Галанте', 'Ч', 'Голова мафії', 'один из ключевых персонажей игры «Mafia Ⅱ». Консильери в семье Фрэнка Винчи. Лео Галанте родился на Сицилии в 1887 году. Вырос Лео вместе со своим лучшим другом Фрэнком Винчи. Парни всё всегда делали вместе. Когда обоим стукнуло по 20, они решили переехать в Америку, где Галанте начал тренироваться и принимать участие в боях за деньги, Винчи же начал устраиваться в «круг» мафии. '),
(7, 'Гордон Фримен', 'Ч', 'Головний протесту цитаделі', 'вымышленный персонаж, главный герой серии игр Half-Life. Имеет учёную степень доктора философии (западный аналог кандидата наук) в области теоретической физики, присужденную ему Массачусетским технологическим институтом (MIT). Работая физиком-теоретиком в научно-исследовательском центре «Чёрная Меза», участвовал при проведении эксперимента над аномальными материалами (англ. anomalous materials), вместе с учёными Айзеком Кляйнером, Илаем Вэнсом и Арнольдом Магнуссоном. В ходе эксперимента произошла ошибка, открывшая межпространственные врата в параллельный мир Зен и положившая начало вторжению на Землю населяющих его обитателей.'),
(8, 'Аликс Вэнс', 'Ж', 'Помічник, технарь', 'персонаж компьютерной игры Half-Life 2, а также её продолжений — Episode One и Episode Two. Аликс является одним из главных неигровых персонажей: она принимает участие в большинстве основных игровых событий Half-Life 2 и сопровождает игрока в нескольких её моментах, а также почти полностью проходит вместе с ним первый и второй эпизоды. Аликс является дочерью Илая Вэнса, командующего Сопротивлением, и вместе с ним и другими персонажами возглавляет эту организацию, противостоящую Альянсу.'),
(9, 'Белла Гот', 'Ж', 'Жіноча роль у грі', ' персонаж, присутствующий во всех играх серии The Sims. Она является дочерью Шимуса и Джокасто Холостякки, сестрой Майкла Холостякки, женой Мортимера Гота и матерью Кассандры и Александра Гот.'),
(10, 'Мортимер Гот', 'Ч', 'Чоловіча роль', 'персонаж, присутствующий в играх The Sims, The Sims на консолях, The Sims Bustin\' Out, The Sims 2, The Sims 3 и The Sims 4.'),
(11, 'Скіфія', 'Ч', 'Військова нація', 'скіфи орієнтуються на війну, тому їхні війська мають переваги проти поранених ворогів, а самі швидко лікуються після перемог. Вони можуть створювати лучників і кінноту в подвійній кількості, а унікальний вершник сакі може бути найнятий одразу без наявності коней. Кургани скіфів приносять прибутки і укріплюють віру, особливо біля пасовищ.'),
(12, 'Выживший', 'Ч', '\"Майстер на всі руки\"', 'Протагонист Fallout 4. В зависимости от выбранного игроком пола персонажа это может быть либо ветеран военных действий в отставке[5], служивший во 2-м батальоне 108-го пехотного полка армии США[6], либо адвокат.'),
(13, 'Нора', 'Ж', 'Помічниця у бункері', ' персонаж Fallout 4, возможный протагонист игры, жена Нейта/Выжившего. Женщина-адвокат[1], имеющая юридическое образование, выпускница «Suffolk County School of Law». Во время военного конфликта между США и Китаем Нора встречается с Нейтом, военным, с которым она знакома с 2076 года[2]. После того, как Нора родила Шона в 2077 году, Нейт увольняется с военной службы и женится на ней.'),
(14, 'Райдер', 'Ч', 'Солдат', 'протагонист игры Mass Effect: Andromeda. Изначально занимает должность специалиста разведки, но позднее становится Первопроходцем Ковчега людей. В отличие от капитана Шепард(а), Райдеров двое — брат и сестра.'),
(15, 'Герои Спарты', 'Ч', 'Військо', 'отряд в Total War: Rome II. Спартанцев с семи лет готовили к жизни воинов, и все свое время они посвящали военным упражнениям. Неудивительно, что спартанцы были великолепно вымуштрованны и прекрасно владели оружием. Тот, кто оставлял щит на поле битвы, считался преступником: если панцирь и шлем защищали самого война, то щит - всю флангу. По боевой дисциплине и организации спартанцы превосходили большинство греческих фаланг. '),
(16, 'Big Smoke', 'Ч', 'Голва банди', 'Мелвін Харріс, більш відомий як Big Smoke, є персонажем у серії Grand Theft Auto, який постає як головний герой і вторинний антагоніст Grand Theft Auto: San Andreas.'),
(17, 'Driver', 'Ч', 'Водій', 'Водій '),
(18, 'Макс Рокатански', 'Ч', 'Божевільний!', '\'Божевільний\' Макс Рокатанскі - головний герой культової серії фільмів \'Божевільний Макс\' режисера Джорджа Міллера і перезапуску вийшов в 2015 році. Головну роль в орігенальной трилогії грає актор Мел Гібсон, в перезапуску його роль зіграв Том Харді.'),
(19, 'Несмертный Джо', 'Ч', 'Супер злочинець', 'Полковник Джо Мур, більш відомий як Несмертний Джо - головний антагоніст фільму Божевільний Макс: Дорога люті. Він також є владикою Цитаделі і лідером воїнів напіврозпаду. Актор, який грав Несмертного Джо, зіграв в першому фільмі Різника'),
(20, 'Лара Крофт', 'Ж', 'Археолог, спортсмен', 'Лара Крофт – главная героиня культовой франшизы, археолог, выпускница Университетского колледжа Лондона.'),
(21, 'Константин', 'Ч', 'Військовий командир', 'Константин – лидер восточноевропейского подразделения вооруженных сил Троицы, командир операции в Сибири. Главный антагонист игры.'),
(22, 'Иона Маиава', 'Ж', 'Помічник археолог', 'Иона Маиава – бывший корабельный кок, путешественник и близкий друг Лары Крофт. Второстепенный персонаж перезапущенной серии Tomb Raider.');
